 -- Create Lookup & Lookup Types table
 -- Gokul - 10/10/2011
 SET @UpdateID = 1
 IF NOT EXISTS(SELECT 1
		FROM DBUpdate
		WHERE UpdateID = @UpdateID)
 BEGIN
    BEGIN TRAN
	BEGIN TRY
		EXEC('
			CREATE TABLE [dbo].[LookupTypes](
				[Id] [tinyint] NOT NULL,
				[Description] [varchar](50) NOT NULL,
			 CONSTRAINT [PK_LookupTypes] PRIMARY KEY CLUSTERED 
			(
				[Id] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
			) ON [PRIMARY]
		')

		EXEC('
			CREATE TABLE [dbo].[Lookup](
				[Id] [int] IDENTITY(1,1) NOT NULL,
				[TypeId] [tinyint] NOT NULL,
				[OriginalId] [int] NOT NULL,
				[DisplayText] [varchar](300) NOT NULL,
				[SortOrder] [int] NULL,
			 CONSTRAINT [PK_Lookup] PRIMARY KEY CLUSTERED 
			(
				[Id] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
			) ON [PRIMARY]
		')

		EXEC('
			CREATE UNIQUE NONCLUSTERED INDEX [IX_Lookup] ON [dbo].[Lookup] 
			(
				[TypeId] ASC,
				[OriginalId] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, SORT_IN_TEMPDB = OFF, IGNORE_DUP_KEY = OFF
			, DROP_EXISTING = OFF, ONLINE = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
		')

		EXEC('
			CREATE TABLE [dbo].[LookupMappings](
				[MappingId] [int] IDENTITY(1,1) NOT NULL,
				[TypeId] [tinyint] NOT NULL,
				[LookupId] [int] NOT NULL,
				[MappedTo] [int] NOT NULL,
			 CONSTRAINT [PK_LookupMappings] PRIMARY KEY CLUSTERED 
			(
				[MappingId] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
			) ON [PRIMARY]
		')

		EXEC('
			CREATE UNIQUE NONCLUSTERED INDEX [IX_LookupMappings] ON [dbo].[LookupMappings] 
			(
				[TypeId] ASC,
				[LookupId] ASC,
				[MappedTo] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, SORT_IN_TEMPDB = OFF, IGNORE_DUP_KEY = OFF
			, DROP_EXISTING = OFF, ONLINE = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
		')

		EXEC('
			CREATE NONCLUSTERED INDEX [IX_LookupMappings_Id] ON [dbo].[LookupMappings] 
			(
				[TypeId] ASC,
				[LookupId] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, SORT_IN_TEMPDB = OFF, IGNORE_DUP_KEY = OFF
			, DROP_EXISTING = OFF, ONLINE = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
		')

		EXEC('
			CREATE NONCLUSTERED INDEX [IX_LookupMappings_MappedTo] ON [dbo].[LookupMappings] 
			(
				[TypeId] ASC,
				[MappedTo] ASC
			)WITH (PAD_INDEX  = OFF, STATISTICS_NORECOMPUTE  = OFF, SORT_IN_TEMPDB = OFF, IGNORE_DUP_KEY = OFF
			, DROP_EXISTING = OFF, ONLINE = OFF, ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = ON) ON [PRIMARY]
		')

		EXEC('
			ALTER TABLE [dbo].[Lookup]  WITH CHECK ADD  CONSTRAINT [FK_Lookup_LookupTypes] FOREIGN KEY([TypeId])
			REFERENCES [dbo].[LookupTypes] ([Id])
		')

		EXEC('
			ALTER TABLE [dbo].[Lookup] CHECK CONSTRAINT [FK_Lookup_LookupTypes]
		')

		EXEC('
			ALTER TABLE [dbo].[LookupMappings]  WITH CHECK ADD  CONSTRAINT [FK_LookupMappings_LookupTypes] FOREIGN KEY([TypeId])
			REFERENCES [dbo].[LookupTypes] ([Id])
		')

		EXEC('
			ALTER TABLE [dbo].[LookupMappings] CHECK CONSTRAINT [FK_LookupMappings_LookupTypes]
		')

