	INSERT INTO [dbo].[SkillsModuleVideos](SMVideoId,[Title],[Text],[F4V],[MP4],[OGV],[Type],[TextPosition])VALUES(@SMVideoID,'Venipuncture - Review','<p><b>Review</b></p><p>We want to make sure you feel comfortable doing this procedure, so here is a wrap-up repeating the key elements you want to incorporate in your routine.</p>','Venipuncture_Review.f4v ','Venipuncture_Review.mp4','Venipuncture_Review.ogv',2,0)
