	INSERT INTO [dbo].[SkillsModuleVideos](SMVideoId,[Title],[Text],[F4V],[MP4],[OGV],[Type],[TextPosition])VALUES(@SMVideoID,'Blood Pressure - Introduction','<p>The vital signs are composed of temperature, pulse, respirations, and blood pressure. Blood pressure is last in the list of vital signs, but not least! It’s a critical noninvasive way to learn about the status of the client’s cardiovascular system.</p><p>Signs are different from symptoms. Symptoms are reported by your client; signs are objective evidence of how your client’s body is functioning. Since some alterations in blood pressure do not produce noticeable symptoms, monitoring the vital sign of blood pressure can give an early warning or signal a positive result of treatment.</p>','BloodPressure_Introduction.f4v','BloodPressure_Introduction.mp4','BloodPressure_Introduction.ogv',2,0)
	SET IDENTITY_INSERT SkillsModuleVideos OFF
	INSERT INTO [dbo].[SMVideoMapping]([TestId],[SMVideoId],[OrderNumber]) VALUES (@TestId,@SMVideoID,2)
	
	Set @SMVideoID = @SMVideoID + 1 --111
