	INSERT INTO [dbo].[SkillsModuleVideos](SMVideoId,[Title],[Text],[F4V],[MP4],[OGV],[Type],[TextPosition])VALUES(@SMVideoID,'Dressing Changes and Sterile Technique - Transparent Dressings','<p><b>Transparent Dressings</b></p><p>A transparent dressing gives a clear protective window outside your client’s wound, for better monitoring. It also provides a waterproof barrier to contaminants. Speaking of waterproofing, before you begin any dressing changes, assess your own need for personal protection equipment (PPE), such as face masks, moisture-proof gowns, and goggles.</p>','Transparent_Dressing.f4v','Transparent_Dressing.mp4','Transparent_Dressing.ogv',2,0)
	SET IDENTITY_INSERT SkillsModuleVideos OFF
