		WHERE Q.ProgramofstudyId = 2 and T.activetest = 1

		-- Inserting remediation which is not present in Lookup table
		INSERT INTO Lookup(TypeId,OriginalId,DisplayText,SortOrder)
		SELECT DISTINCT 15,RemediationID,TopicTitle,0 FROM #DetailsForLookUp
		WHERE TopicTitle not in (SELECT displaytext FROM Lookup where typeid = 15)

		-- Inserting Type 20 – 4,5,6- Topic Mapping
		INSERT INTO Lookupmappings(TypeId,LookupId,MappedTo)
		SELECT DISTINCT 20,CategoryLookupUp,l.Id 
		FROM #DetailsForLookUp d
		INNER JOIN Lookup L ON L.displayText = d.TopicTitle and L.TypeId = 15

		DELETE FROM #DetailsForLookUp 
		WHERE TestId NOT IN(select originalid from Lookup where Typeid=11)

		-- Inserting Type 21 – PN Question Mapping for Remediation
		INSERT INTO Lookupmappings(TypeId,LookupId,MappedTo)
		SELECT DISTINCT 21,LM20.MappingId,d.QId
		FROM Lookupmappings LM20
		INNER JOIN LookUp L ON L.Id = LM20.MappedTo
		INNER JOIN #DetailsForLookUp d ON D.TopicTitle = l.displayText 
		WHERE   L.TypeId = 15 and LM20.TypeId = 20 and LM20.LookupId = d.CategoryLookupUp

		-- InsertingType 22 – PN Question Mapping for Tests
		INSERT INTO Lookupmappings(TypeId,LookupId,MappedTo)
		SELECT DISTINCT 22,LM20.MappingId,d.QId
		FROM Lookupmappings LM20
		INNER JOIN LookUp L ON L.Id = LM20.MappedTo
		INNER JOIN #DetailsForLookUp d ON D.TopicTitle = l.displayText 
		WHERE   L.typeid = 15 and LM20.TypeId = 20 and LM20.lookupid = d.CategoryLookupUp

	

	INSERT INTO DBUpdate (UpdateID, RunOn) VALUES (@UpdateID, GETUTCDATE())
		PRINT 'Update ' + CONVERT(varchar, @UpdateID) + ' Successful. '

		COMMIT TRAN
	END TRY
	BEGIN CATCH
	    ROLLBACK TRAN
